import React, { useState, useEffect, useRef } from 'react';

const LisbonJobOpportunityLanding = () => {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [formStep, setFormStep] = useState(0);
  const [isVisible, setIsVisible] = useState({});
  const [scrollY, setScrollY] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [formData, setFormData] = useState({
    nom: '',
    email: '',
    telephone: '',
    experience: '',
    disponibilite: '',
    motivation: ''
  });

  const observerRef = useRef();
  const heroRef = useRef();

  useEffect(() => {
    document.title = "Poste Francophone Lisbonne - Logement Inclus, Départ 14 jours";

    // Scroll parallax effect
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);

    // Mouse tracking for subtle effects
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);

    // Intersection Observer for animations
    observerRef.current = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          setIsVisible(prev => ({
            ...prev,
            [entry.target.id]: entry.isIntersecting
          }));
        });
      },
      { threshold: 0.1, rootMargin: '50px' }
    );

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  // Auto testimonial rotation
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTestimonial(prev => (prev + 1) % testimonials.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  // Observe elements for animations
  useEffect(() => {
    const elements = document.querySelectorAll('[data-animate]');
    elements.forEach(el => observerRef.current?.observe(el));
    return () => observerRef.current?.disconnect();
  }, []);

  const testimonials = [
    {
      text: "En 6 mois à Lisbonne, j'ai progressé professionnellement et découvert un art de vivre incroyable.",
      author: "Marie D.",
      role: "Conseillère client",
      gradient: "from-pink-500 to-violet-500"
    },
    {
      text: "Le processus était si fluide ! En 2 semaines j'étais installé et opérationnel.",
      author: "Thomas M.", 
      role: "Support technique",
      gradient: "from-blue-500 to-cyan-500"
    },
    {
      text: "Salaire sur 14 mois, assurance privée... Les avantages sont réels.",
      author: "Lucas P.",
      role: "Conseiller senior",
      gradient: "from-green-500 to-emerald-500"
    }
  ];

  const trackEvent = (eventName) => {
    console.log(`FB Pixel: ${eventName}`);
    if (typeof window !== 'undefined' && window.fbq) {
      window.fbq('track', eventName);
    }
  };

  const handleFormSubmit = () => {
    trackEvent('Lead');
    alert('Merci ! Nous vous recontacterons sous 48h.');
  };

  const nextFormStep = () => {
    if (formStep < 2) setFormStep(formStep + 1);
  };

  const prevFormStep = () => {
    if (formStep > 0) setFormStep(formStep - 1);
  };

  const updateFormData = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Dynamic styles with animations
  const getAnimatedStyle = (elementId, baseStyle = {}) => {
    const isElementVisible = isVisible[elementId];
    return {
      ...baseStyle,
      opacity: isElementVisible ? 1 : 0,
      transform: isElementVisible 
        ? 'translateY(0px) scale(1)' 
        : 'translateY(30px) scale(0.95)',
      transition: 'all 0.6s cubic-bezier(0.4, 0, 0.2, 1)'
    };
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: `linear-gradient(135deg, #263F58 0%, #1a2d3f 50%, #0f1419 100%)`,
      color: 'white',
      fontFamily: "'Inter', system-ui, -apple-system, sans-serif",
      position: 'relative',
      overflow: 'hidden'
    },

    // Animated background particles
    backgroundEffect: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      pointerEvents: 'none',
      background: `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255,215,0,0.03) 0%, transparent 50%)`,
      transition: 'background 0.3s ease'
    },

    header: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      zIndex: 50,
      background: `rgba(38, 63, 88, ${scrollY > 50 ? 0.95 : 0.9})`,
      backdropFilter: 'blur(20px) saturate(180%)',
      borderBottom: '1px solid rgba(255,255,255,0.1)',
      padding: scrollY > 50 ? '8px 16px' : '12px 16px',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      boxShadow: scrollY > 50 ? '0 4px 20px rgba(0,0,0,0.2)' : 'none'
    },

    nav: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },

    logo: {
      fontSize: '18px',
      fontWeight: '800',
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },

    navButton: {
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      color: '#1C1C1C',
      padding: '10px 20px',
      borderRadius: '25px',
      fontWeight: '700',
      border: 'none',
      cursor: 'pointer',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      boxShadow: '0 4px 15px rgba(255,215,0,0.3)',
      transform: 'scale(1)'
    },

    heroSection: {
      paddingTop: '100px',
      paddingBottom: '60px',
      paddingLeft: '16px',
      paddingRight: '16px',
      position: 'relative',
      transform: `translateY(${scrollY * 0.1}px)`,
      transition: 'transform 0.1s ease-out'
    },

    maxWidth: {
      maxWidth: '420px',
      margin: '0 auto',
      textAlign: 'center'
    },

    badge: {
      display: 'inline-block',
      background: 'rgba(255,255,255,0.08)',
      border: '1px solid rgba(255,215,0,0.4)',
      borderRadius: '50px',
      padding: '12px 20px',
      marginBottom: '32px',
      fontSize: '14px',
      fontWeight: '600',
      color: '#FFD700',
      backdropFilter: 'blur(10px)',
      boxShadow: '0 8px 32px rgba(255,215,0,0.1)',
      animation: 'pulse 2s infinite'
    },

    title: {
      fontSize: 'clamp(28px, 8vw, 38px)',
      fontWeight: '900',
      lineHeight: '1.1',
      marginBottom: '20px',
      background: 'linear-gradient(135deg, #FFFFFF 0%, #E5E7EB 50%, #FFD700 100%)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      letterSpacing: '-0.02em'
    },

    highlight: {
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },

    subtitle: {
      fontSize: '20px',
      color: '#E5E7EB',
      marginBottom: '12px',
      fontWeight: '600'
    },

    description: {
      color: '#9CA3AF',
      marginBottom: '40px',
      fontSize: '16px'
    },

    offerCard: {
      background: 'rgba(255,255,255,0.08)',
      border: '1px solid rgba(255,215,0,0.3)',
      borderRadius: '20px',
      padding: '28px',
      marginBottom: '40px',
      backdropFilter: 'blur(20px) saturate(180%)',
      boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
      position: 'relative',
      overflow: 'hidden'
    },

    offerCardGlow: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'linear-gradient(45deg, rgba(255,215,0,0.05), transparent, rgba(255,215,0,0.05))',
      animation: 'shimmer 3s ease-in-out infinite'
    },

    offerTitle: {
      color: '#FFD700',
      fontWeight: '800',
      fontSize: '20px',
      marginBottom: '20px',
      position: 'relative',
      zIndex: 2
    },

    offerGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '16px',
      fontSize: '14px',
      position: 'relative',
      zIndex: 2
    },

    offerItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      padding: '8px',
      borderRadius: '10px',
      transition: 'all 0.3s ease'
    },

    checkmark: {
      width: '20px',
      height: '20px',
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#1C1C1C',
      fontSize: '12px',
      fontWeight: 'bold',
      boxShadow: '0 4px 12px rgba(255,215,0,0.3)',
      animation: 'bounce 0.6s ease'
    },

    primaryButton: {
      width: '100%',
      background: 'linear-gradient(135deg, #FFFFFF, #F8F9FA)',
      color: '#1C1C1C',
      padding: '18px',
      borderRadius: '16px',
      fontWeight: '800',
      fontSize: '18px',
      border: 'none',
      cursor: 'pointer',
      marginBottom: '16px',
      boxShadow: '0 8px 32px rgba(0,0,0,0.15)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      position: 'relative',
      overflow: 'hidden'
    },

    secondaryButton: {
      width: '100%',
      background: 'rgba(255,255,255,0.05)',
      color: '#E5E7EB',
      padding: '18px',
      borderRadius: '16px',
      fontWeight: '600',
      border: '1px solid rgba(255,255,255,0.2)',
      cursor: 'pointer',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      backdropFilter: 'blur(10px)'
    },

    section: {
      padding: '80px 16px',
      position: 'relative'
    },

    sectionTitle: {
      fontSize: 'clamp(24px, 6vw, 32px)',
      fontWeight: '800',
      textAlign: 'center',
      marginBottom: '40px',
      background: 'linear-gradient(135deg, #FFFFFF 0%, #FFD700 100%)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },

    statsGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '20px',
      maxWidth: '420px',
      margin: '0 auto'
    },

    statCard: {
      background: 'rgba(255,255,255,0.08)',
      borderRadius: '16px',
      padding: '24px 16px',
      textAlign: 'center',
      border: '1px solid rgba(255,255,255,0.1)',
      backdropFilter: 'blur(20px)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      cursor: 'pointer'
    },

    statNumber: {
      fontSize: '28px',
      fontWeight: '900',
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      marginBottom: '8px'
    },

    statLabel: {
      fontSize: '12px',
      color: '#9CA3AF',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
      fontWeight: '600'
    },

    testimonialCard: {
      background: 'rgba(255,255,255,0.08)',
      borderRadius: '20px',
      padding: '32px',
      border: '1px solid rgba(255,255,255,0.1)',
      marginBottom: '20px',
      backdropFilter: 'blur(20px)',
      position: 'relative',
      overflow: 'hidden',
      minHeight: '200px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center'
    },

    testimonialText: {
      fontStyle: 'italic',
      marginBottom: '24px',
      color: '#E5E7EB',
      fontSize: '16px',
      lineHeight: '1.6',
      position: 'relative',
      zIndex: 2
    },

    testimonialAuthor: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '16px',
      position: 'relative',
      zIndex: 2
    },

    avatar: {
      width: '48px',
      height: '48px',
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#1C1C1C',
      fontWeight: '800',
      fontSize: '18px',
      boxShadow: '0 8px 24px rgba(255,215,0,0.3)'
    },

    dots: {
      display: 'flex',
      justifyContent: 'center',
      gap: '12px',
      marginTop: '24px'
    },

    dot: {
      width: '10px',
      height: '10px',
      borderRadius: '50%',
      cursor: 'pointer',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      border: 'none'
    },

    featureCard: {
      background: 'rgba(255,255,255,0.08)',
      borderRadius: '16px',
      padding: '20px',
      border: '1px solid rgba(255,255,255,0.1)',
      marginBottom: '16px',
      backdropFilter: 'blur(20px)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      cursor: 'pointer'
    },

    featureHeader: {
      display: 'flex',
      alignItems: 'flex-start',
      gap: '16px'
    },

    featureNumber: {
      width: '40px',
      height: '40px',
      background: 'linear-gradient(135deg, #FFD700, #FFA500)',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#1C1C1C',
      fontWeight: '800',
      fontSize: '16px',
      flexShrink: 0,
      boxShadow: '0 6px 20px rgba(255,215,0,0.3)'
    },

    formCard: {
